Appendix: Custom CSS
/* custom-styles.css */
.reveal h1, .reveal h2 {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

/* Scrollable content for long slides */
.scrollable {
  height: 600px;
  overflow-y: auto;
  padding: 20px;
}

/* Better background handling */
.reveal .slides section[data-background] {
  color: white;
}

.reveal .slides section[data-background] h1,
.reveal .slides section[data-background] h2,
.reveal .slides section[data-background] h3 {
  color: white;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
}

/* Timeline styling */
.timeline {
  border-left: 3px solid #667eea;
  padding-left: 20px;
  margin: 20px 0;
}

/* Table styling */
.reveal table {
  font-size: 0.8em;
  border-collapse: collapse;
  width: 100%;
}

.reveal table th,
.reveal table td {
  border: 1px solid #ddd;
  padding: 8px;
  text-align: left;
}

.reveal table th {
  background-color: #667eea;
  color: white;
}

/* Code block styling */
.reveal pre {
  font-size: 0.7em;
  line-height: 1.2em;
}

.reveal code {
  background: #2d2d2d;
  color: #f8f8f2;
  padding: 2px 6px;
  border-radius: 3px;
}

/* Better column spacing */
.reveal .columns {
  display: flex;
  gap: 20px;
  align-items: flex-start;
}

/* Incremental list styling */
.reveal .incremental li {
  margin: 10px 0;
}

/* Fragment visibility fix */
.reveal .fragment {
  visibility: visible !important;
  opacity: 1 !important;
}

.reveal .fragment.visible {
  opacity: 1;
}

/* Center content properly */
.reveal .center {
  text-align: center;
}

/* Better slide padding for content-heavy slides */
.reveal .slides section {
  padding: 20px;
  box-sizing: border-box;
}

/* Responsive font sizing */
@media (max-width: 768px) {
  .reveal .slides section {
    font-size: 0.8em;
  }
}

Appendix: Custom CSS
/* custom-styles.css */
.reveal h1, .reveal h2 {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  font-weight: 800;
}

/* Scrollable content */
.scrollable {
  height: 600px;
  overflow-y: auto;
  padding: 20px;
}

/* Background handling */
.reveal .slides section[data-background] {
  color: white;
}

.reveal .slides section[data-background] h1,
.reveal .slides section[data-background] h2,
.reveal .slides section[data-background] h3 {
  color: white;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
}

/* Timeline styling */
.timeline {
  border-left: 3px solid #667eea;
  padding-left: 20px;
  margin: 20px 0;
  position: relative;
}

.timeline::before {
  content: '';
  position: absolute;
  left: -8px;
  top: 0;
  width: 12px;
  height: 12px;
  background: #667eea;
  border-radius: 50%;
}

/* Table styling */
.reveal table {
  font-size: 0.9em;
  border-collapse: collapse;
  width: 100%;
  margin: auto;
}

.reveal table th,
.reveal table td {
  border: 1px solid #ddd;
  padding: 10px;
  text-align: left;
}

.reveal table th {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  font-weight: 600;
}

.reveal table tr:hover td {
  background-color: rgba(102, 126, 234, 0.1);
}

/* Code block styling */
.reveal pre {
  font-size: 0.8em;
  line-height: 1.3em;
  box-shadow: 0 4px 10px rgba(0,0,0,0.2);
  border-radius: 8px;
}

.reveal code {
  background: #2d2d2d;
  color: #f8f8f2;
  padding: 3px 8px;
  border-radius: 4px;
}

/* Column spacing */
.reveal .columns {
  display: flex;
  gap: 25px;
  align-items: flex-start;
}

/* Incremental lists */
.reveal .incremental li {
  margin: 12px 0;
}

/* Fragment animation */
.reveal .fragment {
  opacity: 0;
}

.reveal .fragment.visible {
  opacity: 1;
  transition: opacity 0.5s ease;
}

/* Center content */
.reveal .center {
  text-align: center;
}

/* Better padding */
.reveal .slides section {
  padding: 25px;
  box-sizing: border-box;
}

/* Responsive sizing */
@media (max-width: 768px) {
  .reveal .slides section {
    font-size: 0.85em;
    padding: 15px;
  }

  .reveal .columns {
    flex-direction: column;
  }
}

/* Special text effects */
.text-gradient {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  font-weight: bold;
}

/* Notes styling */
.reveal .notes {
  font-style: italic;
  color: #666;
  font-size: 0.9em;
}

/* Emphasis box */
.emphasis-box {
  padding: 20px;
  margin: 20px 0;
  background: linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%);
  border-left: 4px solid #667eea;
  border-radius: 8px;
}

/* Quote styling */
.reveal blockquote {
  border-left: 4px solid #667eea;
  padding-left: 20px;
  font-style: italic;
  color: #555;
  margin: 20px 0;
}

